commits.content:
/* General container styles */
.container {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  padding: 16px;
  background-color: #f9f9f9; /* Subtle light background */
  font-family: 'Arial', sans-serif; /* Standard and clean font */
  color: #070c70; /* Consistent primary text color */
  box-sizing: border-box;
}

/* Form wrapper */
.form-wrapper {
  width: 100%;
  max-width: 480px; /* Perfect width for desktop and mobile */
  background-color: #f1d9e5; /* Highlighted form background */
  border-radius: 12px;
  padding: 24px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
  margin: 20px auto;
}

/* Form heading */
.form-header {
  text-align: center;
  margin-bottom: 24px;
}

.form-header h2 {
  font-size: 1.75rem;
  font-weight: bold;
  color: #070c70; /* Matches branding */
}

/* Form structure */
.form-body {
  display: flex;
  flex-direction: column;
  gap: 20px; /* Better spacing for inputs */
}

/* Input fields and labels */
.form-group {
  display: flex;
  flex-direction: column;
}

.label {
  font-size: 15px;
  font-weight: 600;
  color: #070c70;
  margin-bottom: 8px;
}

.input-text,
.input-textarea,
.input-select {
  width: 100%;
  padding: 14px;
  font-size: 15px;
  border-radius: 10px;
  border: 1px solid #ccc;
  background-color: #fff;
  color: #070c70;
  transition: border-color 0.3s ease, box-shadow 0.3s ease;
}

.input-text:focus,
.input-textarea:focus,
.input-select:focus {
  border-color: #ff1493;
  box-shadow: 0 0 5px rgba(255, 20, 147, 0.5);
  outline: none;
}

.input-textarea {
  resize: vertical;
  min-height: 120px;
}

/* Submit button */
.btn-submit {
  width: 100%;
  padding: 14px;
  background-color: #070c70;
  color: #fff;
  font-size: 16px;
  font-weight: bold;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  text-transform: uppercase;
  transition: background-color 0.3s ease, transform 0.2s ease;
}

.btn-submit:hover {
  background-color: #ff1493;
  transform: translateY(-2px);
}

.btn-submit:active {
  background-color: #e31383;
  transform: translateY(1px);
}

/* Error messages */
.errorMsg {
  color: red;
  text-align: center;
  font-size: 14px;
  margin-bottom: 16px;
}

/* Responsive styles */
@media (max-width: 768px) {
  .form-header h2 {
    font-size: 1.5rem;
  }

  .btn-submit {
    font-size: 14px;
    padding: 12px;
  }

  .form-wrapper {
    padding: 18px;
  }
}
