.categories-container {
    padding: 1rem;
    margin: 20px 0;
    border-radius: 10px;
    border: 2px solid #070c70;
    text-align: center;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    background:#ebeefc;
}

.categories-title {
    font-size: 1.8rem;
    color: #070c70;
    margin-bottom: 1.5rem;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 1px;
    animation: fadeIn 1s ease-out;
}

.categories-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
    gap: 1.5rem;
    justify-items: center;
}

@keyframes pulse {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.1);
    }
}

.category-card {
    background: linear-gradient(135deg, #ff1493, #ff66b2);
    color: white;
    padding: 1rem;
    border-radius: 10px;
    cursor: pointer;
    font-size: 1.2rem;
    font-weight: bold;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    box-shadow: 0 4px 8px rgba(255, 20, 147, 0.3);
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    min-width: 150px;
    animation: pulse 4s ease-in-out infinite;
    animation-fill-mode: forwards;
}

.category-card:nth-child(1) { animation-delay: 0.1s; }
.category-card:nth-child(2) { animation-delay: 0.3s; }
.category-card:nth-child(3) { animation-delay: 0.5s; }
.category-card:nth-child(4) { animation-delay: 0.7s; }
.category-card:nth-child(5) { animation-delay: 0.9s; }
.category-card:nth-child(6) { animation-delay: 1.1s; }
.category-card:nth-child(7) { animation-delay: 1.3s; }
.category-card:nth-child(8) { animation-delay: 1.5s; }

.category-card:hover {
    transform: scale(1.15);
    background: linear-gradient(135deg, #070c70, #ff1493);
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

.category-card span {
    display: block;
    transition: color 0.3s ease;
}

.category-card:hover span {
    color: #ffffff;
}
