/* Overall Layout */
.home {
    display: flex;
    flex-direction: column;
    background-color: #f8f9fa;
    font-family: 'Arial', sans-serif;
    min-height: 100vh;
}

.content {
    display: flex;
    justify-content: space-between;
    padding: 20px;
    gap: 20px;
    flex-wrap: wrap;
}

/* Sidebar */
.categories-sidebar {
    width: 250px;
    background-color: #fff;
    border-radius: 10px;
    padding: 15px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    flex-shrink: 0;
}

/* Catalog Section */
.catalog-section {
    flex-grow: 1;
    padding: 15px;
    background-color: #fff;
    border-radius: 10px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.catalog-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr)); /* Auto-adjust columns */
    gap: 20px;
    margin-top: 20px;
}

.catalog-link {
    text-decoration: none;
    color: inherit;
}

.catalog-link:hover .Books {
    transform: scale(1.05);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.15);
}

/* Pagination */
.pagination {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px;
    margin: 20px 0;
}

.pag {
    padding: 10px 15px;
    background-color: #070c70;
    color: #ffffff;
    border: none;
    border-radius: 50%;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.3s ease;
}

.pag:hover:not(:disabled) {
    background-color: #ff1493;
    transform: scale(1.1);
}

.pag:disabled {
    background-color: #cccccc;
    cursor: not-allowed;
}

/* Add Book Button */
.addBook {
    position: fixed;
    bottom: 20px;
    right: 20px;
    background-color: #ff1493;
    color: #fff;
    border: none;
    border-radius: 50%;
    width: 50px;
    height: 50px;
    font-size: 24px;
    display: flex;
    justify-content: center;
    align-items: center;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    cursor: pointer;
    transition: transform 0.3s ease;
}

.addBook:hover {
    transform: scale(1.2);
}

/* Responsive Design */
@media (max-width: 768px) {
    .content {
        flex-direction: column;
        gap: 10px;
        padding: 10px;
    }

    .categories-sidebar {
        width: 100%;
        margin: 0 auto;
        order: 2;
    }

    .catalog-section {
        order: 1;
    }

    .catalog-grid {
        grid-template-columns: 1fr;
        gap: 15px;
        margin: 0 auto;
    }
}

@media (max-width: 480px) {
    .pag {
        padding: 8px;
        width: 40px;
        height: 40px;
    }

    .addBook {
        width: 40px;
        height: 40px;
        font-size: 20px;
    }
}
