+/* General Reset and Styling */
+body {
+    margin: 0;
+    padding: 0;
+    font-family: 'Poppins', sans-serif;
+}
+
+/* Library Image Section */
+.Library1 {
+    width: 100%; /* Adjust to screen size */
+    height: auto; /* Maintain aspect ratio */
+    margin: 0 auto;
+    display: block;
+    max-height: 300px; /* Set max height for mobile */
+}
+
+/* Motto Text */
+.motto {
+    color: #070C70;
+    font-size: 2rem; /* Smaller font for mobile */
+    font-weight: 500;
+    text-align: center;
+    letter-spacing: 1.5px;
+    word-wrap: break-word;
+    margin: 10px auto;
+    animation: text-shadow 1.5s ease-in-out;
+}
+
+.motto:hover {
+    animation: text-shadow 1.5s ease-in-out infinite;
+}
+
+@keyframes text-shadow {
+    0% {
+        transform: translateY(0);
+        text-shadow: 0 0 0 #0c2ffb, 0 0 0 #2cfcfd, 0 0 0 #fb203b, 0 0 0 #fefc4b;
+    }
+    20% {
+        transform: translateY(-1em);
+        text-shadow: 0 0.125em 0 #0c2ffb, 0 0.25em 0 #2cfcfd, 0 -0.125em 0 #fb203b, 0 -0.25em 0 #fefc4b;
+    }
+    40% {
+        transform: translateY(0.5em);
+        text-shadow: 0 -0.0625em 0 #0c2ffb, 0 -0.125em 0 #2cfcfd, 0 0.0625em 0 #fb203b, 0 0.125em 0 #fefc4b;
+    }
+    60% {
+        transform: translateY(-0.25em);
+        text-shadow: 0 0.03125em 0 #0c2ffb, 0 0.0625em 0 #2cfcfd, 0 -0.03125em 0 #fb203b, 0 -0.0625em 0 #fefc4b;
+    }
+    80% {
+        transform: translateY(0);
+        text-shadow: 0 0 0 #0c2ffb, 0 0 0 #2cfcfd, 0 0 0 #fb203b, 0 0 0 #fefc4b;
+    }
+}
+
+/* Motto Container */
+.mottoDiv {
+    display: flex;
+    justify-content: center;
+    align-items: center;
+    width: 90%; /* Adjusted for mobile */
+    margin: 10px auto;
+}
+
+/* Group48 and Rectangle Styling */
+.Group48,
+.Rectangle12 {
+    display: flex;
+    justify-content: center;
+    align-items: center;
+    width: 90%; /* Adjust width for mobile */
+    height: 60px; /* Reduce height for mobile */
+    margin: 10px auto;
+    border-radius: 10px;
+}
+
+.Rectangle12 {
+    border: 2px solid #ff1493; /* Adjust border size */
+    position: relative;
+}
+
+/* Group47 Section */
+.Group47 {
+    display: flex;
+    justify-content: center;
+    align-items: center;
+    width: 80%;
+    height: auto;
+    margin: 10px auto;
+}
+
+/* ExploreBooks Text */
+.ExploreBooks {
+    font-size: 1rem; /* Smaller font for mobile */
+    color: #070C70;
+    text-align: center;
+    text-decoration: none;
+    letter-spacing: 0.5px;
+}
+
+.ExploreBooks:hover {
+    text-decoration: underline;
+}
+
+/* Mobile Responsive Design */
+@media only screen and (max-width: 600px) {
+    .Library1 {
+        height: 200px; /* Reduced height for mobile */
+        width: 100%;
+        margin: 0;
+    }
+
+    .motto {
+        font-size: 1.5rem; /* Adjust font size for smaller screens */
+        margin: 10px;
+    }
+
+    .mottoDiv {
+        width: 100%;
+        margin: 5px auto;
+    }
+
+    .Group47 {
+        width: 90%;
+        margin: 10px auto;
+    }
+
+    .Rectangle12 {
+        width: 90%;
+        height: 50px; /* Smaller height */
+        margin: 10px auto;
+    }
+
+    .ExploreBooks {
+        font-size: 0.9rem; /* Further reduced for compact spacing */
+        text-align: center;
+    }
+}

