     /* Footer */
     footer {
      background-color: #f8f8f8;
      text-align: center;
      padding: 20px 0;
      color: #070C70;
      width: 100%;
    }
    .grid {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      gap: 1.5em; /* Adjust the spacing between links */
      margin-bottom: 1.5em;
    } 
    .footer-link {
      text-decoration: none;
      color: #070C70;
      font-size: 0.9rem; /* Adjust the font size for smaller screens */
    }
    .footer-link:hover {
      color: #135665;
      text-decoration: underline;
    }
    .media-link {
      text-decoration: none;
      margin: 0 5px;
    }
    .media-link svg {
      font-size: 1.8rem; /* Adjust icon size for mobile */
    }
    hr {
      margin: 20px auto;
      width: 80%; /* Make it narrower for smaller screens */
    }
    @media (max-width: 768px) {
      .grid {
        flex-direction: column;
        align-items: center;
        gap: 1em; /* Reduce the spacing for better alignment */
      }
      .media-link svg {
        font-size: 1.5rem; /* Reduce icon size for smaller devices */
      }
      .footer-link {
        font-size: 0.8rem; /* Reduce text size for smaller screens */
      }
      footer {
        padding: 15px 10px;
      }
      hr {
        width: 90%; /* Slightly wider on smaller screens */
      }
    }
    


