/* navbar*/
.logo{ 
    width:75px;
    height:75px;
    margin-top: 11px;
    margin-left: 30px;
    }
    .okinLogo{ 
      width: 6em;
      height: 4em;
      /* margin-top: 10px; */
      }
    .siteName{ 
     font-size: 2.5em;
     font-family:fantasy;
     transform: translateY(-32px);
     border-width: 5px;
     display: flex;
    }
    .navbar{ 
      display:flex;
       flex-direction:row; 
       max-height:95px;
       gap:10px;
       background-color: white;
     }
     @media (max-width: 768px) {
      .logo{ 
        width: 50px;
        height: 50px;
        margin-top: 20px;
        margin-left: 0;
        }
        .siteName{ 
         font-size: 2em;
         margin-top: 15px;
        }
        .navbar{ 
          display:flex;
           flex-direction:row; 
           justify-content:center;
       }
      }





     /* Footer */
     footer {
      background-color: #f8f8f8;
      text-align: center;
      padding: 20px 0;
      color: #070C70;
      width: 100%;
    }
    .grid {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      gap: 1.5em; /* Adjust the spacing between links */
      margin-bottom: 1.5em;
    } 
    .footer-link {
      text-decoration: none;
      color: #070C70;
      font-size: 0.9rem; /* Adjust the font size for smaller screens */
    }
    .footer-link:hover {
      color: #135665;
      text-decoration: underline;
    }
    .media-link {
      text-decoration: none;
      margin: 0 5px;
    }
    .media-link svg {
      font-size: 1.8rem; /* Adjust icon size for mobile */
    }
    hr {
      margin: 20px auto;
      width: 80%; /* Make it narrower for smaller screens */
    }
    @media (max-width: 768px) {
      .grid {
        flex-direction: column;
        align-items: center;
        gap: 1em; /* Reduce the spacing for better alignment */
      }
      .media-link svg {
        font-size: 1.5rem; /* Reduce icon size for smaller devices */
      }
      .footer-link {
        font-size: 0.8rem; /* Reduce text size for smaller screens */
      }
      footer {
        padding: 15px 10px;
      }
      hr {
        width: 90%; /* Slightly wider on smaller screens */
      }
    }
    


