+import Navbar from '../layout/Navbar.jsx';
+import Footer from '../layout/Footer.jsx';
+
+export const Donate = () => {
+
+  const [donationAmount, setDonationAmount] = useState("");
+  const [donorDetails, setDonorDetails] = useState({ name: "", email: "" });
+
+  const handleInputChange = (e) => {
+    const { name, value } = e.target;
+    setDonorDetails({ ...donorDetails, [name]: value });
+  };
+
+  const handlePayment = () => {
+    const PAYPAL_CLIENT_ID = "YOUR_PAYPAL_CLIENT_ID";
+
+    const paypalScript = document.createElement("script");
+    paypalScript.src = `https://www.paypal.com/sdk/js?client-id=${PAYPAL_CLIENT_ID}&currency=USD`;
+    paypalScript.async = true;
+
+    paypalScript.onload = () => {
+      window.paypal
+        .Buttons({
+          createOrder: (data, actions) => {
+            return actions.order.create({
+              purchase_units: [
+                {
+                  amount: {
+                    value: donationAmount,
+                  },
+                },
+              ],
+            });
+          },
+          onApprove: (data, actions) => {
+            return actions.order.capture().then(() => {
+              alert("Thank you for your donation!");
+            });
+          },
+        })
+        .render("#paypal-button");
+    };
+
+    document.body.appendChild(paypalScript);
+  };
+
+
+    return (
+        <div>
+      <Navbar/>
+      <div style={{ padding: "2rem", maxWidth: "600px", margin: "0 auto" }}>
+      <h1>Donate to LiteLouge</h1>
+      <p>Your support makes a difference. Thank you!</p>
+
+      <form
+        onSubmit={(e) => {
+          e.preventDefault();
+          handlePayment();
+        }}
+      >
+        <div>
+          <label>Name</label>
+          <input
+            type="text"
+            name="name"
+            value={donorDetails.name}
+            onChange={handleInputChange}
+            required
+            placeholder="Your Name"
+          />
+        </div>
+
+        <div>
+          <label>Email</label>
+          <input
+            type="email"
+            name="email"
+            value={donorDetails.email}
+            onChange={handleInputChange}
+            required
+            placeholder="Your Email"
+          />
+        </div>
+
+        <div>
+          <label>Donation Amount (USD)</label>
+          <input
+            type="number"
+            value={donationAmount}
+            onChange={(e) => setDonationAmount(e.target.value)}
+            required
+            placeholder="Amount"
+            min="1"
+          />
+        </div>
+
+        <button type="submit" style={{ margin: "1rem 0", padding: "0.5rem 1rem" }}>
+          Donate
+        </button>
+      </form>
+
+      <div id="paypal-button" />
+    </div>
+      <Footer/>      
+        </div>
+      );
+}
+export default Donate;
